/**
 * @fileoverview Error definitions.
 * @module
 * @see https://stackoverflow.com/a/32749533/5893085
 */
/**
 * @class ExtendedError
 * @description Extended error.
 * @protected
 */
declare class ExtendedError extends Error {
    /**
     * @description Create a custom (extended) error.
     * @param {string} message Error message
     * @param {...*} params Extra parameters
     */
    constructor(message: string, ...params: any[]);
}
/**
 * @class TransactionError
 * @augments ExtendedError
 */
declare class TransactionError extends ExtendedError {
}
/**
 * @class BlockError
 * @augments ExtendedError
 */
declare class BlockError extends ExtendedError {
}
/**
 * @class OutOfBoundsError
 * @augments ExtendedError
 */
declare class OutOfBoundsError extends ExtendedError {
}
export { TransactionError, BlockError, OutOfBoundsError };
