import fse from 'fs-extra';
import path from 'path'
// import { FileService } from '../services'
// const fsv = new FileService()

import { fileURLToPath } from 'url';
import { dirname } from 'path';
const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);

let VERSION = {major: 0, minor: 0, patch: 0}
const filePath = path.resolve(__dirname + '../../../version.json')
if (fse.existsSync(filePath)){
    const content = fse.readFileSync(filePath).toString()
    // VERSION = fsv.convertToJsonWithComment(content)
    // console.log(VERSION)
    VERSION = JSON.parse(content)
}

export {
    VERSION
}